import SwiftUI

struct Privacy: View {
    @Binding var privacyAgree: Bool
    @Environment(\.presentationMode) var presentationMode: Binding<PresentationMode>


    var body: some View {
        VStack(alignment: .leading){
            Spacer()
            Text("개인정보 처리방침")
            privacyInfo()
            Text("이용 약관")
            termsOfUse()
            Button(action: {
                self.privacyAgree = true
                self.presentationMode.wrappedValue.dismiss()
                
            }) {
                Text("동의합니다.")
                    .foregroundColor(.white)
                    .background(Color.mainBlue)
                    .font(Font.custom("S-CoreDream-5Medium", size: 15))
                    .frame(width: 100, height: 50)
            }
            Spacer()
        }
    }
    
    fileprivate func privacyInfo() -> some View {
        return(
            ScrollView{
                Text("")
                    .frame(width: UIScreen.main.bounds.width / 1.5, height: UIScreen.main.bounds.height / 3, alignment: .leading)
                    .border(Color.gray)
            }
        )
    }
    
    fileprivate func termsOfUse() -> some View {
        return(
            ScrollView{
                Text("")
                    .frame(width: UIScreen.main.bounds.width / 1.5, height: UIScreen.main.bounds.height / 3, alignment: .leading)
                    .border(Color.gray)
            }
        )
    }
    
    
}

